            </div>
            <div className="form-example">
                <label htmlFor="email">Enter your email: </label>
                <input type="email" name="email" id="email" required/>
            </div>
            <div className="form-example">
                <input type="submit" value="Subscribe!"/>
            </div>
        </form>
    )
}
export default ClientForm;
