import React, {useEffect, useState} from "react";
import {FieldValues, useForm} from "react-hook-form";

interface MessageFormProps {
    messageFormNeedUpdate: () => void
}

const ClientForm: React.FC<MessageFormProps> = ({messageFormNeedUpdate}: MessageFormProps) => {
    const {register, handleSubmit} = useForm();
    const [content, setContent] = useState("");

    const onSubmit = (data: FieldValues) => {
        setContent(data.content);
        messageFormNeedUpdate();
    }

    return (
        <form onSubmit={handleSubmit((data: FieldValues) => onSubmit(data, this))}>
            <h4>Rentrez un message : </h4>
            <div>
                <input {...register("content")} placeholder="Message"/>
                <button type="submit">Envoyer</button>
            </div>
        </form>
    )
}
export default ClientForm;
